import base64
from dataclasses import dataclass

from cerebellum.browser import Coordinate

# Base64 encoded cursor image
CURSOR_64 = ""
CURSOR_BYTES = base64.b64decode(CURSOR_64)

@dataclass(frozen=True)
class ScalingRatio:
    ratio_x: float
    ratio_y: float
    old_size: Coordinate
    new_size: Coordinate


@dataclass(frozen=False)
class MsgOptions:
    mouse_position: bool
    screenshot: bool
    tabs: bool